 (-1,'1548653295217',32,'admin',NULL,0,'','root',1,0,0,0,'rx','日薪','personId,bookId,year,month','import cn.js.fan.util.*;\nimport cn.js.fan.db.*;\nimport com.cloudwebsoft.framework.db.*;\nimport com.redmoon.oa.hr.*;\nimport com.redmoon.oa.visual.*;\nimport com.redmoon.oa.sys.*;\nimport com.redmoon.oa.oacalendar.*;\nimport java.util.*;\nimport java.sql.SQLException;\n\nint personId = {$personId};\nint bookId = {$bookId};\nint year = {$year};\nint month = {$month};\n\nString ret=\"0\", errMsg = \"\";\n\nFormDAO fdao = PersonMgr.getPerson(personId);\nString userName = fdao.getFieldValue(\"user_name\");\nString realName = fdao.getFieldValue(\"realname\");\nDate bd = DateUtil.getDate(year, month-1, 1);\nint days = DateUtil.getDayCount(year, month-1);\nDate ed = DateUtil.addDate(bd, days-1);\n\nJdbcTemplate jt = new JdbcTemplate();\nString formula = \"#pos(\" + personId + \")\";\n// DebugUtil.log(\"script\", \"formula\", \"formula=\" + formula);\nString pos = \"\";\ntry {\n	FormulaResult fr = FormulaUtil.render(formula);\n	pos = fr.getValue();\n} catch (ErrMsgException e) {\n	e.printStackTrace();\n}\nif (\"\".equals(pos)) {\n    errMsg = realName + \" 的岗位未设置！\";\n    return;\n}\n\n// 取得工资设置表中对应的岗位和绩效\ndouble pv = 0, jx=0;\nString sql = \"select pv,jx from form_table_salary_set where pos=\" + StrUtil.sqlstr(pos);\nResultIterator ri = jt.executeQuery(sql);\nif (ri.hasNext()) {\n    ResultRecord rr = (ResultRecord)ri.next();\n    pv = rr.getDouble(1);\n    jx = rr.getDouble(2);\n}\n\n// 如果工资表已生成，则取表中的绩效\nsql = \"select jx from salary_payroll where person_id=? and book_id=? and year=? and month=?\";\ntry {\n    ri = jt.executeQuery(sql, new Object[]{personId, bookId, year, month});\n    if (ri.hasNext()) {\n        ResultRecord rr = (ResultRecord)ri.next();\n        jx = rr.getDouble(1);\n    }\n}\ncatch (SQLException e) {\n    // 工资表未生成的时候，字段为空\n}\n\n// 取得本月的工作日\nOACalendarDb oac = new OACalendarDb();\ndouble workDays = oac.getWorkDayCountFromDb(DateUtil.addDate(bd, -1), ed);\n\nret = NumberUtil.round((pv + jx)/ workDays, 2);\n\n\n\n\n\n\n\n\n','6','2','#rx(personId)','script'),
