 (-1,'1548653264911',34,'admin',NULL,0,'','root',1,0,0,0,'ysjts','月事假天数','personId,year,month','import cn.js.fan.util.*;\nimport cn.js.fan.db.*;\nimport com.cloudwebsoft.framework.db.*;\nimport com.redmoon.oa.hr.*;\nimport com.redmoon.oa.visual.*;\nimport com.redmoon.oa.sys.*;\nimport java.util.*;\n\nint personId = {$personId};\nint year = {$year};\nint month = {$month};\n\nString ret=\"0\", errMsg = \"\";\n\nFormDAO fdao = PersonMgr.getPerson(personId);\nString userName = fdao.getFieldValue(\"user_name\");\nString realName = fdao.getFieldValue(\"realname\");\nDate bd = DateUtil.getDate(year, month-1, 1);\nint days = DateUtil.getDayCount(year, month-1);\nDate ed = DateUtil.addDate(bd, days-1);\n\ndouble sjts = 0; // 事假天数\nJdbcTemplate jt = new JdbcTemplate();\n/*\nString sql = \"select sum(tripday) from kaoqin_arrange where name=? and date>=? and date<=?\";\nResultIterator ri = jt.executeQuery(sql, new Object[]{userName, bd, ed});\nif (ri.hasNext()) {\n    ResultRecord rr = (ResultRecord)ri.next();\n    ccts = rr.getDouble(1);\n}*/\n\nString sql = \"select qjkssj, qjjssj, qjkssj_ampm, qjjssj_ampm, jqlb from form_table_qjsqd where applier=? and qjkssj <=? and qjjssj >= ? and cws_status = 1\";\nResultIterator ri = jt.executeQuery(sql, new Object[]{userName, ed, bd});\nwhile (ri.hasNext()) {\n    ResultRecord rr = (ResultRecord)ri.next();\n    // 取出本月的天数\n    Date startDate = rr.getDate(1);\n    Date endDate = rr.getDate(2);\n    String startAmpm = rr.getString(3);\n    String endAmpm = rr.getString(4);\n    String jqlb = rr.getString(5);\n    \n    if (!\"事假\".equals(jqlb)) {\n        continue;\n    }\n    \n    if (DateUtil.compare(startDate, bd)==2) {\n        startDate = bd;\n    }\n    if (DateUtil.compare(endDate, ed)==1) {\n        endDate = ed;\n    }\n    \n    try {\n        sjts += SignMgr.getWorkDays(startDate, startAmpm, endDate, endAmpm);\n    }\n    catch (ErrMsgException e) {\n        errMsg = e.getMessage();\n        return;\n    }\n}\n\nret = String.valueOf(sjts);\n\n\n\n\n','6','2','#sjts(personId,year,month)','script'),
 (-1,'1548653222391',35,'admin',NULL,0,'','root',1,0,0,0,'ccbt','出差补贴','personId,year,month','import cn.js.fan.util.*;\nimport cn.js.fan.db.*;\nimport com.cloudwebsoft.framework.db.*;\nimport com.redmoon.oa.hr.*;\nimport com.redmoon.oa.visual.*;\nimport com.redmoon.oa.sys.*;\nimport java.util.*;\n\nint personId = {$personId};\nint year = {$year};\nint month = {$month};\n\nString ret=\"0\", errMsg = \"\";\n\nFormDAO fdao = PersonMgr.getPerson(personId);\nString userName = fdao.getFieldValue(\"user_name\");\nString realName = fdao.getFieldValue(\"realname\");\nDate bd = DateUtil.getDate(year, month-1, 1);\nint days = DateUtil.getDayCount(year, month-1);\nDate ed = DateUtil.addDate(bd, days-1);\n\ndouble ccts = 0; // 出差天数\n/*\nString sql = \"select sum(tripday) from kaoqin_arrange where name=? and date>=? and date<=?\";\nResultIterator ri = jt.executeQuery(sql, new Object[]{userName, bd, ed});\nif (ri.hasNext()) {\n    ResultRecord rr = (ResultRecord)ri.next();\n    ccts = rr.getDouble(1);\n}*/\n\ntry {\n    ccts = SignMgr.getTripDays(userName, year, month);\n}\ncatch (ErrMsgException e) {\n    errMsg = e.getMessage();\n    return;\n}\n\nString formula = \"#pos(\" + personId + \")\";\n// DebugUtil.log(\"script\", \"formula\", \"formula=\" + formula);\nString pos = \"\";\ntry {\n	FormulaResult fr = FormulaUtil.render(formula);\n	pos = fr.getValue();\n} catch (ErrMsgException e) {\n	e.printStackTrace();\n}\nif (\"\".equals(pos)) {\n    errMsg = realName + \" 的岗位未设置！\";\n    return;\n}\n\ndouble ccbtPerDay = 0;\nString sql = \"select ccbt from form_table_salary_set where pos=\" + StrUtil.sqlstr(pos);\nJdbcTemplate jt = new JdbcTemplate();\nResultIterator ri = jt.executeQuery(sql);\nif (ri.hasNext()) {\n    ResultRecord rr = (ResultRecord)ri.next();\n    ccbtPerDay = rr.getDouble(1);\n}\n\nDebugUtil.log(\"script\", \"ccts\", realName + \" ccts=\" + ccts + \" ccbtPerDay=\" + ccbtPerDay);\nret = NumberUtil.round(ccbtPerDay * ccts, 2);\n\n\n\n\n\n\n\n\n','9','2','#ccbt(personId,year,month)','script'),
 (-1,'1549686159043',36,'admin',NULL,0,'','root',1,0,0,0,'tax','所得税','gz','import com.cloudwebsoft.framework.db.*;\nimport cn.js.fan.db.*;\nimport cn.js.fan.util.*;\nimport java.util.*;\nimport com.redmoon.oa.flow.FormDb;\nimport com.redmoon.oa.visual.*;\nimport com.redmoon.oa.hr.*;\nimport com.redmoon.oa.sys.*;\n\nString ret = \"\", errMsg = \"\";\n/*\nint personId = {$personId};\nint bookId = {$bookId};\nint year = {$year};\nint month = {$month};\n*/\n\ndouble gz = {$gz};\n\ndouble tax = 0;\nret = \"0\";\n\ndouble taxRate = 0.0;\nint sskcs=0;\nString sql = \"select tax_rate,sskcs from form_table_salary_tax where lower_limit<=\" + gz + \" and upper_limit>=\" + gz;\nJdbcTemplate jt = new JdbcTemplate();\nResultIterator ri = jt.executeQuery(sql);\nif (ri.hasNext()) {\n    ResultRecord rr = (ResultRecord)ri.next();\n    taxRate = rr.getDouble(1);\n    sskcs = rr.getInt(2);\n	DebugUtil.log(\"script\", \"tax\", \"-------------------taxRate=\" + taxRate);\n    // 工资个税的计算公式为：应纳税额 = ( 工资薪金所得 - 五险一金 - 扣除数 ) × 适用税率 - 速算扣除数\n	tax = taxRate * gz / 100 - sskcs;\n}\nelse {\n    errMsg = \"所得税率设置表中没有区间包括\" + gz + \"的记录\";\n}		\nret = NumberUtil.round(tax, 2);\n\n','9','2','#tax(gz)','script'),
 (-1,'1548653182610',37,'admin',NULL,0,'','root',1,0,0,0,'selBookPerson','选择帐套中的人员','bookId, userNames','import com.redmoon.oa.person.*;\nimport cn.js.fan.util.*;\nimport com.cloudwebsoft.framework.db.*;\nimport cn.js.fan.db.*;\nimport com.redmoon.oa.flow.*;\n\nString ret = \"\";\nString errMsg = \"\";\nString userNames = \"{$userNames}\";\nlong bookId = {$bookId};\nString[] ary = StrUtil.split(userNames, \",\");\nif (ary==null) {\n    errMsg = \"请选择人员\";\n}\n\nSystem.out.println(\"script: userNames=\" + userNames + \" bookId=\" + bookId);\n\nJdbcTemplate jt = new JdbcTemplate();\njt.setAutoClose(false);\ntry {\n    // 检查是否有重复\n    String sql = \"select sp.id, p.realname from form_table_salary_bk_person sp, form_table_personbasic p where sp.cws_id=\"\n                + bookId + \" and sp.person_id=p.id and p.user_name=?\";\n    for (int i=0; i<ary.length; i++) {\n        String userName = ary[i];\n        ResultIterator ri = jt.executeQuery(sql, new Object[]{userName});\n        if (ri.hasNext()) {\n            ResultRecord rr = (ResultRecord)ri.next();\n            String realName = rr.getString(2);\n            errMsg = realName + \" 已被选择\";\n            return;\n        }\n    }\n    sql = \"select id,sex from form_table_personbasic where user_name=?\";\n    \n    // String sqlBkPerson = \"insert into form_table_salary_bk_person (person_id,sex) values (?,?)\";\n    FormDb fd = new FormDb(\"salary_bk_person\");\n    com.redmoon.oa.visual.FormDAO fdao = new com.redmoon.oa.visual.FormDAO(fd);\n    \n    for (int i=0; i<ary.length; i++) {\n        String userName = ary[i];\n        ResultIterator ri = jt.executeQuery(sql, new Object[]{userName});\n        if (ri.hasNext()) {\n            ResultRecord rr = (ResultRecord)ri.next();\n            int personId = rr.getInt(1);\n            String sex = rr.getString(2);\n            fdao.setFieldValue(\"person_id\", String.valueOf(personId));\n            fdao.setFieldValue(\"sex\", sex);\n            fdao.setCwsId(String.valueOf(bookId));\n            fdao.create();\n        }\n    }\n}\nfinally {\n    jt.close();\n}\n\n\n\n\n\n\n\n\n\n\n','0','2','选择帐套中的人员\n用法：\nselBookPerson(bookId, userNames)\n人员帐户以半角逗号分隔，如果userNames中有多个人，则userNames必须加引号，如果只有1个人，则不需加引号','script'),
